// Generated by CoffeeScript 2.7.0
module.exports.run = function(client, message, args) {
    let currentprefix = client.db.get("serversPrefix").value()[message.channel.guild.id];
    // Check if there is a message to send
    if (!args[0]) {
        if (!currentprefix)
        {
            return client.createMessage(message.channel.id, `:x: Bạn chưa gõ dấu lệnh mới, lệnh hiện tại vẫn là :\`${client.config.prefix}\``);
        }
        else
        {
            return client.createMessage(message.channel.id, `Lệnh hiện tại là :\`${currentprefix}\``);
        }
            
    } else {
        client.config.prefix = args[0];
        // Change the prefix
        client.db.set(`serversPrefix.${message.channel.guild.id}`, args[0]).write();

        return client.createMessage(message.channel.id, `:o: Lệnh của Sim đã được đổi thành: \`${args[0]}\`,gõ ${args[0]}help để biết thêm`);
    }
};

module.exports.help = {
    name: "prefix",
    onlyMod: true
};
